    {


        return $this->db->query('SELECT items.id,items.title,items.sub_title,items.image,(select title from Categories where id=items.cat_id) as catname,program_item.day,program_item.meal
from items INNER JOIN program_item ON items.id=program_item.item_id
WHERE items.id IN (SELECT items.id FROM `program_item` where program_id=' . $id . ')
AND program_item.program_id =' . $id)->result_array();

    }

