     * Get all program_item
     */
    function get_all_program_item($params = array())
    {
        $this->db->order_by('id', 'desc');
        if(isset($params) && !empty($params))
        {
            $this->db->limit($params['limit'], $params['offset']);
        }
        return $this->db->get('program_item')->result_array();
    }





